import { Routes, Route, Link } from "react-router-dom";

import './App.css';
import  "./logo.png";
import "./grid1.png";


function Home() {
  return (
    <>
       <div className="first-section">
    <h1 className="title-name">Hi I 'm Abdan Hafidz</h1>
    <h1 className="title-name">Junior Website Developer</h1>
      <a href="#more"><button className="btn-primary">More About Me</button></a>
    </div>
    <div className="grid1"></div>
    <h1 align="center" className="title-name">I also do</h1>
    <div className="alsodo">
      <div className="cp">
        <h1 className="text-danger title-name"><li>Competitive Programming</li> </h1>



        </div>
        <div className="infor">
        <h1 className="text-info title-name"><li>Informatics Olympiad</li> </h1>



        </div>
        <div className="cr">
        <h1 className="text-success title-name"><li>Content Creator</li> </h1>



        </div>

        <div className="grid2"></div>


    </div>
  <div className="second-section">
    <h1 align="center" className="title-name">Abdan Hafidz, Glad To See You!</h1>

    <p align="center" className="text-primary">For more or less 3 years I have explored my coding skills to the end to pursue website development and the last 2 years I have worked in several institutions in the section of website development, IT division, full-stack development. And since I was in high school, I began to deepen my abilities in the field of computer science, until in the end I often won several National level competitions in the field of computer science or better known as Informatics in my country. I often have fun on the competitive programming platforms codeforces, atcoder, TLX, Vjduges and others. Not infrequently I also take part in National competitions with competitive programming categories and website design :) I do social media content creating too.</p>
    <div className="middle ctc" align="center">
      <div className="middle-sub-4">

      <button className="btn-white-oval btn-rip ripple">My Codeforces</button> 
      </div>
      <div className="middle-sub-4">
      <button className="btn-white-oval btn-rip ripple">TLX</button>   
      </div>
      <div className="middle-sub-4">
      <button className="btn-white-oval btn-rip ripple">Github</button> 
      </div>
      </div>

  </div>
  <div className="first-section">
    <h1 className="title-name">My Skills</h1>

    <br></br>

    <h2>Competitive Programming</h2>
    <div className="bar-cp"></div> 70%

  </div>
    </>
  );
}
function About() {
  return (
    <>
      <main>
        <h2>Who are we?</h2>
        <p>
          That feels like an existential question, don't you
          think?
        </p>
      </main>
      <nav>
        <Link to="/">Home</Link>
      </nav>
    </>
  );
}
function App() {

  return (

<div className="sabeb">
    <div className="navbar">
    <Link className="navbar-brand" to="." ><img src={'./logo.png'} /></Link>

    <div className="nav-link">


          <div className="nav-item">
            <a linkto="about/">About Me</a>
          </div>
          <div className="nav-item">
          <Link to="about/">Skills</Link>
          </div>
          <div className="nav-item">
          <a linkto="about/">Portofolio</a>
          </div>
          <div className="nav-item">
          <a linkto="about/"> Projects </a>
          </div>
          <div className="nav-item">
          <a linkto="about/"> Socials </a>
          </div>
          <div className="nav-item">
          <a linkto="about/"> Blog </a>
          </div>
    </div>
    <div className="bar">
  <span className="icon-bar">

  </span>
  </div>
</div>





  <Routes>
  <Route path="/" element={<Home />} />
  <Route path="about" element={<About />} />
</Routes>

  </div>
  );
}

var buttons = document.getElementsByClassName('btn-rip');

Array.prototype.forEach.call(buttons, function(b){
  b.addEventListener('click', createRipple);
})

function createRipple(e)
{
  if(this.getElementsByClassName('ripple').length > 0)
    {
      this.removeChild(this.childNodes[1]);
    }

  var circle = document.createElement('div');
  this.appendChild(circle);

  var d = Math.max(this.clientWidth, this.clientHeight);
  circle.style.width = circle.style.height = d + 'px';

  circle.style.left = e.clientX - this.offsetLeft - d / 2 + 'px';
  circle.style.top = e.clientY - this.offsetTop - d / 2 + 'px';

  circle.classList.add('ripple');
}
export default App;
