import { Routes, Route, Link } from "react-router-dom";
import React from "react"
import { Chrono } from "react-chrono";
import './App.css';
import  "./logo.png";
import "./grid1.png";
import "./icon-cpp.png";


const Chronoi = () => {
  const title=<h2>Full Stack Developer</h2>

  const items = [{
    title: "2020 - Now",
    cardTitle: <h2>Indonesia Science Student Training Foundation</h2>,
    url: "https://www.forpelindo.com",
    cardSubtitle:"<h2 class='title-name'>Full Stack Developer</h2>",
    cardDetailedText: title,
    media: {
      type: "IMAGE",
      source: {
        url: "https://www.forpelindo.com/_public/assets/images/5logo1.png"
      }
    }
  },
  {
    title: "2020 - Now",
    cardTitle: "Indonesia Science Student Training Foundation",
    url: "https://www.forpelindo.com",
    cardSubtitle:"Full Stack Developer",
    cardDetailedText: "Full Stack Developer",
    media: {
      type: "IMAGE",
      source: {
        url: "https://www.forpelindo.com/_public/assets/images/5logo1.png"
      }
    }
  },



];

